mapper.delete:
DELETE FROM USER_IMAGE
 WHERE USER_ID      = #{userId}
   AND IMAGE_USAGE  = #{imageUsage}

mapper.insert:
INSERT INTO USER_IMAGE (
                        USER_ID
                      , IMAGE_DATA
                      , IMAGE_NAME
                      , IMAGE_USAGE
                        )
VALUES (
        #{userId}
      , #{imageData, jdbcType=BLOB}
      , #{imageName}
      , #{imageUsage}
        )

mapper.select:
SELECT IMAGE_DATA
  FROM USER_IMAGE
 WHERE USER_ID      = #{userId}
   AND IMAGE_USAGE  = #{imageUsage}
